package cn.edu.heu.emms.javabean;

public class EmmsEng {
         private int engno;
         private String engname;
         private int engtask;
         
		public EmmsEng(int engno, String engname, int engtask) {
			super();
			this.engno = engno;
			this.engname = engname;
			this.engtask = engtask;
		}
		
		
         


		public EmmsEng() {
			super();
			// TODO Auto-generated constructor stub
		}





		public int getEngno() {
			return engno;
		}

		public void setEngno(int engno) {
			this.engno = engno;
		}

		public String getEngname() {
			return engname;
		}

		public void setEngname(String engname) {
			this.engname = engname;
		}

		public int getEngtask() {
			return engtask;
		}

		public void setEngtask(int engtask) {
			this.engtask = engtask;
		}

		
        
		
        
}
